INSERT INTO `base_gallery` VALUES (16,'The Netherlands','\r\n    \r\n    \r\n	    \r\n	    is a constituent country of the Kingdom of the Netherlands, located mainly in North-West Europe and with some islands in the Caribbean.<br>','en',1),(17,'Spain','is a sovereign state and a member of the European Union located in southwestern Europe on the Iberian Peninsula.','en',2),(18,'Italy','\n	    \n	    \n	    \n	    \n	    is a unitary parliamentary republic in South-Central Europe. To the \nnorth, it borders France, Switzerland, Austria, and Slovenia along the \nAlps. To the south, it consists of the untirety of the Italian \nPeninsula, Sicily, Sardinia–the two largest islands in the Mediterranean\n Sea–and many other smaller islands.						','en',3),(905,'Нидерланды','\r\n    \r\n    \r\n	    \r\n	    является составной страна Королевства Нидерландов, расположенные в основном в Северо-Западной Европе и некоторых островов в Карибском море.<br>','ru',1),(906,'Нідерланди','\r\n    \r\n    \r\n	    \r\n	    є складовою країна Королівства Нідерландів, розташовані в основному в Північно-Західній Європі та деяких островів у Карибському морі.<br>','ua',1),(907,'Aux Pays-Bas','\r\n    \r\n    \r\n	    \r\n	    est un pays constituant le Royaume des Pays-Bas, situés principalement dans le Nord-Ouest de l&#39;Europe et avec quelques îles dans les Caraïbes.<br>','fr',1),(908,'Niederlande','\r\n    \r\n    \r\n	    \r\n	    ist ein Bestandteil Land des Königreichs der Niederlande, vor allem in Nord-Westeuropa und einigen Inseln in der Karibik.<br>','de',1),(909,'Los Países Bajos','\r\n    \r\n    \r\n	    \r\n	    es un país constituyente del Reino de los Países Bajos, que se encuentra principalmente en el noroeste de Europa y con algunas islas del Caribe.<br>','es',1),(910,'Paesi Bassi','\r\n    \r\n    \r\n	    \r\n	    è un paese costituente del Regno dei Paesi Bassi, che si trova principalmente in Europa nord-occidentale e con alcune isole dei Caraibi.<br>','it',1),(911,'Οι Κάτω Χώρες','\r\n    \r\n    \r\n	    \r\n	    αποτελεί συστατικό χώρα του Βασιλείου των Κάτω Χωρών, που βρίσκεται κυρίως στη Βόρεια-Δυτική Ευρώπη και με ορισμένα νησιά της Καραϊβικής.<br>','gr',1),(912,'荷兰','\r\n    \r\n    \r\n	    \r\n	    是国家主要集中在西北欧和加勒比地区的一些岛屿，位于荷兰王国的一个组成部分。<br>','ch',1),(913,'オランダ','\r\n    \r\n    \r\n	    \r\n	    主に北&#183;西ヨーロッパとカリブ海のいくつかの島を持つ位置、オランダ王国の構成国である。<br>','jp',1),(914,'Hollanda','\r\n    \r\n    \r\n	    \r\n	    özellikle Kuzey-Batı Avrupa ve Karayipler&#39;deki bazı adalar ile bulunan Hollanda, İngiltere kurucu bir ülkedir.<br>','tr',1),(915,'هولندا','\r\n    \r\n    \r\n	    \r\n	    بلد المكونة للمملكة هولندا، وتقع أساسا في شمال غرب أوروبا ومع بعض الجزر في منطقة البحر الكاريبي.<br>','ar',1),(916,'هلند','\r\n    \r\n    \r\n	    \r\n	    تشک&#1740;ل &#1740;ک کشور پادشاه&#1740; هلند، عمدتا در شمال غرب اروپا و برخ&#1740; از جزا&#1740;ر کارائ&#1740;ب واقع شده است.<br>','fa',1),(917,'הולנד','\r\n    \r\n    \r\n	    \r\n	    היא מדינה מכוננת של ממלכת הולנד, הממוקם בעיקר בצפון מערב אירופה ועם כמה איים בים הקריבי.<br>','il',1),(918,'De Nederland','\r\n    \r\n	    \r\n	    is een bestanddeel land van het Koninkrijk der Nederland, voornamelijk gelegen in Noord-West-Europa en met een aantal eilanden in het Caribisch gebied. is een bestanddeel land van het Koninkrijk der Nederland, gelegen\r<br>vooral in Noord-West-Europa en met een aantal eilanden in het Caribisch gebied.','nl',1),(919,'Holandia','\r\n    \r\n    \r\n	    \r\n	    jest składnikiem kraj Królestwa Holandii, znajduje się głównie w północno-zachodniej Europy oraz z niektórych wysp na Karaibach.<br>','pl',1),(920,'Холандия','\r\n    \r\n    \r\n	    \r\n	    е съставна страна на Кралство Холандия, разположени предимно в Северозападна Европа и с някои острови в Карибско море.<br>','bg',1),(921,'Nīderlande','\r\n    \r\n    \r\n	    \r\n	    ir sastāvdaļa valsts no Nīderlandes Karalistes, kas galvenokārt atrodas ziemeļu-rietumu Eiropā un ar dažām Karību salās.<br>','lv',1),(922,